     8	};
     9	
    10	const insertAtCaret = (target: HTMLElement, text: string) => {
    11	    const selection = window.getSelection();
    12	    if (selection?.rangeCount) {
    13	        const range = selection.getRangeAt(0);
    14	        range.deleteContents();
    15	        const node = document.createTextNode(text);
    16	        range.insertNode(node);
    17	
    18	        // Move caret to the end of the newly inserted text node.
    19	        range.setStart(node, node.length);
    20	        range.setEnd(node, node.length);
    21	        selection.setBaseAndExtent(range.startContainer, range.startOffset, range.endContainer, range.endOffset);
    22	
    23	        // Dispatch input event to trigger Markdown Input to parse the new text
    24	        target.dispatchEvent(new Event('input', {bubbles: true}));
    25	    } else {
    26	        insertByCommand(text);
    27	    }
    28	};
    29	
    30	const useHtmlPaste: UseHtmlPaste = (textInputRef, preHtmlPasteCallback, removeListenerOnScreenBlur = false) => {
    31	    const navigation = useNavigation();
    32	
    33	    /**
    34	     * Set pasted text to clipboard
    35	     * @param {String} text
    36	     */
    37	    const paste = useCallback((text: string) => {
    38	        try {
    39	            const textInputHTMLElement = textInputRef.current as HTMLElement;
    40	            if (textInputHTMLElement?.hasAttribute('contenteditable')) {
    41	                insertAtCaret(textInputHTMLElement, text);
    42	            } else {
    43	                insertByCommand(text);
    44	            }
    45	
    46	            // Pointer will go out of sight when a large paragraph is pasted on the web. Refocusing the input keeps the cursor in view.
    47	            // To avoid the keyboard toggle issue in mWeb if using blur() and focus() functions, we just need to dispatch the event to trigger the onFocus handler
    48	            // We need to trigger the bubbled "focusin" event to make sure the onFocus handler is triggered
    49	            textInputHTMLElement.dispatchEvent(
    50	                new FocusEvent('focusin', {
    51	                    bubbles: true,
    52	                }),
    53	            );
    54	            // eslint-disable-next-line no-empty
    55	        } catch (e) {}
    56	        // We only need to set the callback once.
    57	        // eslint-disable-next-line react-compiler/react-compiler, react-hooks/exhaustive-deps
    58	    }, []);
    59	
    60	    /**
    61	     * Manually place the pasted HTML into Composer
    62	     *
    63	     * @param {String} html - pasted HTML
    64	     */
    65	    const handlePastedHTML = useCallback(
    66	        (html: string) => {
    67	            paste(Parser.htmlToMarkdown(html));
    68	        },
    69	        [paste],
    70	    );
    71	
    72	    /**
    73	     * Paste the plaintext content into Composer.
    74	     *
    75	     * @param {ClipboardEvent} event
    76	     */
    77	    const handlePastePlainText = useCallback(
    78	        (event: ClipboardEvent) => {
    79	            const plainText = event.clipboardData?.getData('text/plain');
    80	            if (plainText) {
    81	                paste(plainText);
    82	            }
    83	        },
    84	        [paste],
    85	    );
    86	
    87	    const handlePaste = useCallback(
    88	        (event: ClipboardEvent) => {
    89	            if (!textInputRef.current) {
    90	                return;
    91	            }
    92	
    93	            if (preHtmlPasteCallback?.(event)) {
    94	                return;
    95	            }
    96	
    97	            const isFocused = textInputRef.current?.isFocused();
    98	
    99	            if (!isFocused) {
   100	                return;
   101	            }
   102	
   103	            event.preventDefault();
   104	
   105	            const TEXT_HTML = 'text/html';
   106	
   107	            // If paste contains HTML
   108	            if (event.clipboardData?.types?.includes(TEXT_HTML)) {
   109	                const pastedHTML = event.clipboardData.getData(TEXT_HTML);
   110	
   111	                const domparser = new DOMParser();
   112	                const embeddedImages = domparser.parseFromString(pastedHTML, TEXT_HTML).images;
   113	
   114	                // Exclude parsing img tags in the HTML, as fetching the image via fetch triggers a connect-src Content-Security-Policy error.
   115	                if (embeddedImages.length > 0 && embeddedImages[0].src) {
   116	                    // If HTML has emoji, then treat this as plain text.
   117	                    if (embeddedImages[0].dataset && embeddedImages[0].dataset.stringifyType === 'emoji') {
   118	                        handlePastePlainText(event);
   119	                        return;
   120	                    }
   121	                }
   122	                handlePastedHTML(pastedHTML);
   123	                return;
   124	            }
   125	            handlePastePlainText(event);
   126	        },
   127	        // eslint-disable-next-line react-compiler/react-compiler, react-hooks/exhaustive-deps
   128	        [handlePastedHTML, handlePastePlainText, preHtmlPasteCallback],
   129	    );
   130	
   131	    useEffect(() => {
   132	        // we need to re-register listener on navigation focus/blur if the component (like Composer) is not unmounting
   133	        // when navigating away to different screen (report) to avoid paste event on other screen being wrongly handled
   134	        // by current screen paste listener
   135	        let unsubscribeFocus: () => void;
   136	        let unsubscribeBlur: () => void;
   137	        if (removeListenerOnScreenBlur) {
   138	            unsubscribeFocus = navigation.addListener('focus', () => document.addEventListener('paste', handlePaste, true));
   139	            unsubscribeBlur = navigation.addListener('blur', () => document.removeEventListener('paste', handlePaste, true));
   140	        }
   141	
   142	        document.addEventListener('paste', handlePaste, true);
   143	
   144	        return () => {
   145	            if (removeListenerOnScreenBlur) {
   146	                unsubscribeFocus();
   147	                unsubscribeBlur();
   148	            }
   149	            document.removeEventListener('paste', handlePaste, true);
   150	        };
